current.custom_css:
.sf-footer form.sf__footer-form-newsletter .sf__newletter__form-input {
border-radius: 0;
border-color: #222;
}

.sf-footer__block .sf__footer-block-title,
.sf-home__hero .sf-hero__title,
.sf-hero__subtitle,
.slide__block-subtitle,
.content__subheading,
.section__subheading {
font-weight: 700;
}
.content__subheading:not(.text-white),
.section__subheading:not(.text-white) {
	color: #666;
}

.sf__footer-block-our_store .social-media-links a {
padding: 0 20px;
}

.sf__footer-block-our_store svg path {
    fill: #000;
}

@media ( min-width: 768px ) {
.sf__footer-block-newsletter .sf__footer-block-title {
font-size: 42px;
}
}
